import React, { Component } from 'react';
import Calendar from './calendar';
import Tabs from './tabs';

class MainBody extends Component {
    state = {  
        initialTabs: [
                            {id: "1", description: "Your Scheduled games"},
                            {id: "2", description: "Your trackers"},
                            {id: "3", description: "Find games"},
                            {id: "4", description: "Profile Info"},
                            {id: "5", description: "Info"}
                            ],

        display: "auto display"

    } 
    render() { 
        return (
            <React.Fragment>


                <div class="flex-item-mainBody flex-item-mainBody-1" >
                    <Tabs
                     tabsToDisplay = { this.state.initialTabs } 
                     handleTabClick = { this.state.handleTabClick }
                     />
                </div>

                <div class="flex-item-mainBody flex-item-mainBody-2">
                    <Calendar display={this.state.display}/>
                </div>


            </React.Fragment>


        );
    }

    handleTabClick = (id) => {
        var newDisplay="";
        if (id === "1") { newDisplay = "ONE"; }
        else if (id==="2") {newDisplay = "TWO"}
        else if (id==="3") {newDisplay="THREE"}
        else if (id==="4") {newDisplay="four"}
        else if (id==="5") {newDisplay="five"}
        this.setState({display:newDisplay})
    }
}

export default MainBody;
