    state = {  

        tabsToDisplay: [
            {id: "1", description: "Your games"},
            {id: "2", description: "Find Games"},
            {id: "3", description: "Find past games"},
            {id: "4", description: "Profile Info"},
            {id: "5", description: "Info"}
            ],

