import React, { Component, useEffect, useState } from 'react';
import Display from './display';
import Tabs from './tabs';
import SearchBar from './searchBar';

import axios from 'axios';

class MainBody extends Component {
    state = {  

        bodyToDisplay: [
                {name: "", region: "", season: ""},
        ],

        tabsToDisplay: [
            {id: "1", description: "Your Scheduled games"},
            {id: "2", description: "Your trackers"},
            {id: "3", description: "Find games"},
            {id: "4", description: "Profile Info"},
            {id: "5", description: "Info"}
            ],

        defaultTabs: [
            {id: "1", description: "Your Scheduled games"},
            {id: "2", description: "Your trackers"},
            {id: "3", description: "Find games"},
            {id: "4", description: "Profile Info"},
            {id: "5", description: "Info"}
            ],

        searchByTabs: [
            {id: "back", description: "Back"},
            {id: "sbt-1", description: "Search by League"},
            {id: "sbt-2", description: "Search by Teams"},
            {id: "sbt-3", description: "Search by Player"},
            {id: "sbt-4", description: "Search by Region"}
        ],

        leagueTabs: [
            {id: "back", description: "Back"},
            {id: "premier", description: "Premier League"},
            {id: "champions", description: "UEFA Champions League"},
            {id: "bundesliga", description: "Bundesliga"},
            {id: "serie-a", description: "Serie A"},
            {id: "la-liga", description: "La Liga"}
        ],

        scheduledGamesTabs: [
            {id: "back", description: "Back"},
            {id: "6", description: "Game 1"},
            {id: "7", description: "Game 2"},
            {id: "8", description: "Game 3"},
            {id: "9", description: "Game 4"}
            ],

            testTabs: [
                {id: "back", description: "Back"},
                {id: "6", description: "TEST"},
                {id: "7", description: "TEST 2"},
                {id: "8", description: "TEST 3"},
                {id: "9", description: "TEST 4"}
                ],

        displayData: {name: "", region: "", season: ""},

        defaultDisplayData: {}

    } 
    render() { 
        return (
            <React.Fragment>


                <div class="flex-item-mainBody flex-item-mainBody-1 flex-container-tabs" >

                    <Tabs
                     tabsToDisplay = { this.state.tabsToDisplay } 
                     handleTabClick = { this.handleTabClick }
                     />
                </div>

                <div class="flex-item-mainBody flex-item-mainBody-2 flex-container-display">
                    <Display name={this.state.displayData}/>
                </div>


            </React.Fragment>


        );
    }

    handleTabClick = (id) => {
        var newDisplay="";
        var newTabs = this.state.tabsToDisplay;
        if (id ==="back") { newDisplay="auto display";  newTabs= this.state.defaultTabs; } 

        else if (id === "1") { this.getScheduledGames();  
        }
        else if (id==="2") {newDisplay = "TWO";
        }
        else if (id==="3") {newDisplay="THREE"; newTabs = this.state.searchByTabs;}
        else if (id==="4") {newDisplay="four"}
        else if (id==="5") {newDisplay="five"}

        else if (id==="sbt-1") { newTabs = this.state.leagueTabs; }
        else if (id==="sbt-2") {}
        else if (id==="sbt-3") {}
        else if (id==="sbt-4") {}

        else if (id==="premier" || id==="champions" || id==="bundesliga" || id==="serie-a" || id==="la-liga") { this.getLeagueInfo(id); }





        this.setState({
            display: newDisplay,
            tabsToDisplay: newTabs
        })
    }

    getScheduledGames = () => {
          axios({
          method: 'get',
          url: "http://localhost:8080/api/all-events"
        })
        .then(result => {
            let events = [{id: "back", description: "Back"}];

            // mapping data in each event in json to "events" array
            result.data.forEach( res => {
                events.push({id:String(res.eventName), description: String(res.eventDescription)});
            })

            this.setState({ tabsToDisplay: events});
        })
        .catch(error => console.error("err:", error));

      }

      //api call to find available games for each region
      getLeagueInfo = (leagueId) => {

        console.log("looking at leafue id",leagueId);

        if (leagueId==="premier") leagueId="39";
        else if (leagueId==="champions") leagueId="2";
        else if (leagueId==="bundesliga") leagueId="78";
        else if (leagueId==="serie-a") leagueId="135";
        else if (leagueId==="la-liga") leagueId="140";

        const options = {
          method: 'GET',
          url: 'https://api-football-v1.p.rapidapi.com/v3/leagues',
          params: {id: leagueId},
          headers: {
            'X-RapidAPI-Key': 'c3997e6e90mshc57e13968b48304p10dd78jsnd81c29d9c57a',
            'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
          }
        };

        let name;
        let region;
        let season;
        axios.request(options).
        then(response => {
            let res = response.data.response[0];
            console.log(res);
            name = res.league.name;
            region = res.country.name;
            console.log("name: ",name);
            console.log("region: ",region);
            season = res.seasons.filter(season => season.current===true );
            season=season[0];
            season = season.start+' to '+season.end;
            console.log("season: ",season);

        }).catch(function (error) {
            console.error(error);
        });


      }
}

export default MainBody;
