import React, { Component } from 'react';
import EventTab from './eventTab';
import '../App.css';
//props:
// fixtures a list of objects where each object is an event
// addGame (fnction reference)
class EventTabs extends Component {
    state=  {
        showLogIn: false,
        showRegistration: false
    }
    render() { 
        return (
            <React.Fragment>
                <div class={this.getClasses("tabs")}>
                {
                    this.props.fixtures.map( event => (
                        <div key={ event.id } class="eventTab">
                            <EventTab key={event.id} 
                            id = {event.id}
                            home ={event.home}
                            away ={event.away}
                            date = {event.date}
                            timezone ={event.timezone}
                            location = {event.location}
                            stadium = {event.stadium}
                            homeImg = {event.homeImg}
                            awayImg = {event.awayImg}
                            time = {event.time}
                            addGame = {this.props.addGame}
                            homeScore = {event.homeScore}
                            awayScore = {event.awayScore}
                            displayButton={this.props.displayButton}
                            />
                        </div>

                    ))
                }
                </div>

                {/**display login/registration message */}
                <div class={this.getClasses("message")}>
                    <h1>You are not logged in!</h1>
                    <div class="login-buttons">
                        <button onClick = {this.setState({showLogIn: true})}>
                            <p>Log in</p>
                        </button>
                        <button onClick ={this.setState({showRegistration: true})}>
                            <p>Register</p>
                        </button>
                    </div>     
                </div>

                {/**log in gui */}
                <div class={this.getClasses("login")}>
                    <h3>Username</h3>
                    <h4>Password</h4>
                </div>

                {/** registration gui */}
                <div class="display-none">
                    <h3>First Name</h3>
                    <h3>Last Name</h3>
                    <h3>Username</h3>
                    <h4>Pasword</h4>
                </div>
            </React.Fragment>
        );
    }

    getClasses = (divType) =>
    {
        if (divType==="tabs") {
            if (this.props.isLoggedIn) return "";
            else return "display-none;"
        }

        if (divType==="message") {
            if (this.props.isLoggedIn===true) return "display-none";
            else return "login-message";
        }

        if (divType==="login") {
            if (this.state.showLogIn===false) return "display-none";
            else return "login-gui";
        }

        if (divType==="registration") {
            if (this.state.showRegistration===false) return "display-none";
            else return "registration-gui";
        }


    }
}

export default EventTabs;
