import React, { Component, useEffect, useState } from 'react';
import Calendar from './calendar';
import Tabs from './tabs';

import axios from 'axios';

class MainBody extends Component {
    state = {  

        tabsToDisplay: [
            {id: "1", description: "Your Scheduled games"},
            {id: "2", description: "Your trackers"},
            {id: "3", description: "Find games"},
            {id: "4", description: "Profile Info"},
            {id: "5", description: "Info"}
            ],

        defaultTabs: [
            {id: "1", description: "Your Scheduled games"},
            {id: "2", description: "Your trackers"},
            {id: "3", description: "Find games"},
            {id: "4", description: "Profile Info"},
            {id: "5", description: "Info"}
            ],

        scheduledGamesTabs: [
            {id: "back", description: "Back"},
            {id: "6", description: "Game 1"},
            {id: "7", description: "Game 2"},
            {id: "8", description: "Game 3"},
            {id: "9", description: "Game 4"}
            ],

            testTabs: [
                {id: "back", description: "Back"},
                {id: "6", description: "TEST"},
                {id: "7", description: "TEST 2"},
                {id: "8", description: "TEST 3"},
                {id: "9", description: "TEST 4"}
                ],

        display: "auto display"

    } 
    render() { 
        return (
            <React.Fragment>


                <div class="flex-item-mainBody flex-item-mainBody-1" >
                    <Tabs
                     tabsToDisplay = { this.state.tabsToDisplay } 
                     handleTabClick = { this.handleTabClick }
                     />
                </div>

                <div class="flex-item-mainBody flex-item-mainBody-2">
                    <Calendar display={this.state.display}/>
                </div>


            </React.Fragment>


        );
    }

    handleTabClick = (id) => {
        var newDisplay="";
        var newTabs = this.state.defaultTabs;
        if (id ==="back") { newDisplay="auto display"; }
        else if (id === "1") { 
            newDisplay = "ONE"; 
            newTabs = this.state.scheduledGamesTabs; 
            let sportsData = this.getRes();  
            console.log("testing");
            console.log(sportsData);
        }

        else if (id==="2") {newDisplay = "TWO"}
        else if (id==="3") {newDisplay="THREE"}
        else if (id==="4") {newDisplay="four"}
        else if (id==="5") {newDisplay="five"}
        this.setState({
            display: newDisplay,
            tabsToDisplay: newTabs
        })
    }

    getRes = () => {
        return axios({
          method: 'get',
          url: "http://localhost:8080/api/all-events"
        })
        .then(result => {
            this.setState({ tabsToDisplay: scheduledGamesTabs});
        })
        .catch(error => console.error("err:", error));

      }
}

export default MainBody;
